INSERT INTO `product_translation` (`id`, `translatable_id`, `title`, `short_desc`, `description`, `locale`, `how_to_use`, `ingredient`, `sub_title`) VALUES
(6, 6, 'กระดาษเย็นเช็ดผิวกาย พร้อมระงับกลิ่นกาย กลิ่นคูลซิตรัส', NULL, '<ul>\r\n	<li>เช็ดผิวกาย คลายความเหนอะหนะ ขจัดกลิ่นกายที่เกิดจากเหงื่อ ช่วยให้ผิวสะอาด สดชื่นนาน เย็นสบายได้ทันที</li>\r\n	<li>มีส่วนผสมของสารยับยั้งเหงื่อ กลิ่นกาย และแบคทีเรีย ช่วยควบคุมเหงื่อและกลิ่นกาย</li>\r\n	<li>มีส่วนผสมของเมนทอลให้ความรู้สึกเย็น สดชื่นนาน</li>\r\n	<li>เนื้อกระดาษหนานุ่ม ขนาดใหญ่พิเศษ 25x20 ซม. พอดีกับการใช้ได้ทั่วตัว</li>\r\n	<li>กลิ่นซิตรัส หอมสดชื่น</li>\r\n	<li>เหมาะสำหรับใช้เมื่อต้องการความเย็นสดชื่น อากาศร้อน เวลาเร่งรีบ หลังออกกำลังกาย ก่อนไปนัดสำคัญ หลังตื่นนอน ก่อนออกไปข้างนอก ใช้ที่โรงเรียน ที่ทำงาน หรือระหว่างการเดินทาง เพื่อผ่อนคลายความเหนื่อยล้าและตอนที่ไม่สามารถอาบน้ำได้</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>ปริมาณสุทธิ 10 แผ่น (ราคา 120 บาท)</p>\r\n\r\n<p>&nbsp;</p>', 'th', NULL, NULL, NULL),
(7, 8, 'บอดี้ วอเทอร์ ให้ความเย็นสำหรับผิวกาย \r\nกลิ่นไอซ์ฟรุ้ตตี้', NULL, '<ul>\r\n	<li>บอดี้ วอเทอร์ ฉีดร่างกายเพื่อให้ความรู้สึกเย็น สดชื่น สบายตัว　</li>\r\n	<li>ทำให้รู้สึกเย็นได้อย่างรวดเร็วและเย็นต่อเนื่องยาวนาน และสามารถกลับมารู้สึกเย็นได้อีกทุกครั้งที่เหงื่อออก</li>\r\n	<li>สามารถฉีดได้ แม้ขวดจะอยู่ในลักษณะคว่ำลง ดังนั้น จึงง่ายต่อการใช้ฉีดแผ่นหลัง</li>\r\n	<li>กลิ่นไอซ์ฟรุ้ตตี้</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>ปริมาณสุทธิ 170 มล. (ราคา 270 บาท)</p>', 'th', NULL, NULL, NULL),
